// Check if data.series exists
if (data.series && data.series.length > 0) {
  const eventTypes = data.series[0].fields[0].values;
  const authors = data.series[0].fields[1].values;
  const repoNames = data.series[0].fields[2].values;
  const total = data.series[0].fields[3].values; // Assuming you have a field named "Total"

  // Create nodes and links
  const nodes = [];
  const links = [];

  authors.forEach((author, index) => {
    const sourceNode = {
      name: author,
      category: 0, // Category for source nodes
      symbolSize: 60, // Size for source nodes
    };

    const eventTypeNode = {
      name: eventTypes[index],
      category: 1, // Category for eventType nodes
      symbolSize: 40, // Size for eventType nodes
    };

    const repoNode = {
      name: repoNames[index],
      category: 2, // Category for repo nodes
      symbolSize: 30, // Size for repo nodes
    };

    const totalNode = {
      name: `Total: ${total[index]}`, // Assuming you have an array "total"
      category: 3, // Category for total nodes
      symbolSize: 20, // Size for total nodes
    };

    // Ensure source, eventType, repo, and total nodes are unique before adding them
    if (!nodes.some((node) => node.name === sourceNode.name)) {
      nodes.push(sourceNode);
    }

    if (!nodes.some((node) => node.name === eventTypeNode.name)) {
      nodes.push(eventTypeNode);
    }

    if (!nodes.some((node) => node.name === repoNode.name)) {
      nodes.push(repoNode);
    }

    if (!nodes.some((node) => node.name === totalNode.name)) {
      nodes.push(totalNode);
    }

    // Create links between author, eventType, repo, and total nodes
    links.push({
      source: author,
      target: eventTypes[index],
    });

    links.push({
      source: eventTypes[index],
      target: repoNames[index],
    });

    links.push({
      source: eventTypes[index],
      target: totalNode.name,
    });
  });

  // Create categories for nodes
  const categories = [
    {
      name: 'Authors',
    },
    {
      name: 'Event Type',
    },
    {
      name: 'Repo Names',
    },
    {
      name: 'Total', // Add a category for "Total" nodes
    },
  ];

  // Create ECharts option
  const option = {
    tooltip: {
      trigger: 'item',
      formatter: '{b}',
    },
    legend: {
      x: 'left',
      data: ['Authors', 'Event Types', 'Repo Names', 'Total'], // Add "Total" to legend data
    },
