      {
        type: 'graph',
        layout: 'force',
        roam: true,
        label: {
          show: true,
          textStyle: {  // Add textStyle property to configure text style
            color: '#000', // Set the text color to a brighter color, such as white (#FFF)
          },
        },
        force: {
          repulsion: 100,
          gravity: 0.1,
          edgeLength: 150,
        },
        data: nodes,
        links: links,
        draggable: true,
        categories: categories,
        edgeSymbol: ["none", "arrow"],

        // Increase the size of arrow marks
